import React, { useState, useEffect } from "react";
import axios from "axios";

const ReglementationSearch = () => {
  const [search, setSearch] = useState("");
  const [titre, setTitre] = useState("");
  const [titres, setTitres] = useState([]);
  const [results, setResults] = useState([]);

  // üîπ R√©cup√©rer tous les titres disponibles au chargement
  useEffect(() => {
    const fetchTitres = async () => {
      try {
        const res = await axios.get("http://localhost:3001/api/reglementation/titres"); // endpoint pour r√©cup√©rer les titres
        setTitres(res.data);
      } catch (err) {
        console.error("Erreur lors de la r√©cup√©ration des titres :", err);
      }
    };
    fetchTitres();
  }, []);

  const handleSearch = async () => {
    try {
      const params = { search, titre };
      const res = await axios.get("http://localhost:3001/api/reglementation", { params });
      setResults(res.data);
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la recherche !");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Recherche R√©glementation</h1>

      <input
        type="text"
        placeholder="Mot-cl√©"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ marginRight: 10 }}
      />

     

      <button onClick={handleSearch} style={{ marginLeft: 10 }}>Rechercher</button>

      <div style={{ marginTop: 20 }}>
        <h2>R√©sultats :</h2>
        {results.length === 0 ? (
          <p>Aucun r√©sultat</p>
        ) : (
          <ul>
            {results.map((item) => (
              <li key={item.id}>
                <strong>{item.titre}</strong> - {item.domaine}
                <p>{item.exigence}</p>
                <p>{item.lois}</p>
                <p>{item.documents}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default ReglementationSearch;
