import React, { useState } from "react";
import axios from "axios";

const ReglementationSearch = () => {
  const [search, setSearch] = useState("");
  const [domaine, setDomaine] = useState("");
  const [chapitre, setChapitre] = useState("");
  const [sousChapitre, setSousChapitre] = useState("");
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    try {
      const params = { search, domaine, chapitre, sous_chapitre: sousChapitre };
      const res = await axios.get("http://localhost:3001/api/reglementation", { params });
      setResults(res.data);
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la recherche !");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Recherche Réglementation</h1>
      
      <input type="text" placeholder="Mot-clé" value={search} onChange={(e) => setSearch(e.target.value)} />
      <input type="text" placeholder="Domaine" value={domaine} onChange={(e) => setDomaine(e.target.value)} />
      <input type="text" placeholder="titre" value={chapitre} onChange={(e) => setChapitre(e.target.value)} />
      <input type="text" placeholder="Sous-chapitre" value={sousChapitre} onChange={(e) => setSousChapitre(e.target.value)} />

      <button onClick={handleSearch}>Rechercher</button>

      <div style={{ marginTop: 20 }}>
        <h2>Résultats :</h2>
        {results.length === 0 ? <p>Aucun résultat</p> :
          <ul>
            {results.map((item) => (
              <li key={item.id}>
                <strong>{item.titre}</strong> - {item.domaine} / {item.chapitre} / {item.sous_chapitre}
                <p>{item.exigence}</p>
                <p>{item.lois}</p>
                <p>{item.documents}</p>
              </li>
            ))}
          </ul>
        }
      </div>
    </div>
  );
};

export default ReglementationSearch;
