import React, { useState, useEffect } from "react";
import axios from "axios";

const ReglementationSearch = () => {
  const [search, setSearch] = useState("");
  const [titre, setTitre] = useState("");
  const [titres, setTitres] = useState([]);
  const [domaines, setDomaines] = useState([]);
  const [selectedDomaine, setSelectedDomaine] = useState("");
  const [results, setResults] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);

  // üîπ R√©cup√©rer les titres uniques
  useEffect(() => {
    const fetchTitres = async () => {
      try {
        const res = await axios.get("http://localhost:3001/api/reglementation/titres");
        setTitres(res.data.map(item => item.titre));
      } catch (err) {
        console.error("Erreur r√©cup√©ration titres :", err);
      }
    };
    fetchTitres();
  }, []);

  // üîπ R√©cup√©rer les domaines uniques
  useEffect(() => {
    const fetchDomaines = async () => {
      try {
        const res = await axios.get("http://localhost:3001/api/reglementation/domaines");
        setDomaines(res.data.map(item => item.domaine));
      } catch (err) {
        console.error("Erreur r√©cup√©ration domaines :", err);
      }
    };
    fetchDomaines();
  }, []);

  // üîπ Filtrage frontend par domaine s√©lectionn√©
  useEffect(() => {
    let filtered = results;
    if (selectedDomaine) {
      filtered = filtered.filter(item => item.domaine === selectedDomaine);
    }
    setFilteredResults(filtered);
  }, [selectedDomaine, results]);

  // üîπ Requ√™te recherche backend
  const handleSearch = async () => {
    try {
      const params = { search, titre, domaine: selectedDomaine };
      const res = await axios.get("http://localhost:3001/api/reglementation", { params });
      setResults(res.data);
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la recherche !");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Recherche R√©glementation</h1>

      <input
        type="text"
        placeholder="Mot-cl√©"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ marginRight: 10 }}
      />

      <select value={titre} onChange={(e) => setTitre(e.target.value)} style={{ marginRight: 10 }}>
        <option value="">-- Choisir un titre --</option>
        {titres.map((t, i) => (
          <option key={i} value={t}>{t}</option>
        ))}
      </select>

      <select value={selectedDomaine} onChange={(e) => setSelectedDomaine(e.target.value)} style={{ marginRight: 10 }}>
        <option value="">-- Choisir un domaine --</option>
        {domaines.map((d, i) => (
          <option key={i} value={d}>{d}</option>
        ))}
      </select>

      <button onClick={handleSearch}>Rechercher</button>

      <div style={{ marginTop: 20 }}>
        <table border="1" cellPadding="5" style={{ marginTop: 20, borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              <th>Titre</th>
              <th>Domaine</th>
              <th>Exigence</th>
              <th>Lois</th>
              <th>Documents</th>
            </tr>
          </thead>
          <tbody>
            {filteredResults.length === 0 ? (
              <tr>
                <td colSpan="5" style={{ textAlign: "center" }}>Aucun r√©sultat</td>
              </tr>
            ) : (
              filteredResults.map((item) => (
                <tr key={item.id}>
                  <td>{item.titre}</td>
                  <td>{item.domaine}</td>
                  <td>{item.exigence}</td>
                  <td>{item.lois}</td>
                  <td>{item.documents}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ReglementationSearch;
