import React, { useState, useEffect } from "react";
import axios from "axios";

const ReglementationSearch = () => {
  const [search, setSearch] = useState("");
  const [titre, setTitre] = useState("");
  const [selectedDomaine, setSelectedDomaine] = useState("");
  const [allData, setAllData] = useState([]);
  const [titres, setTitres] = useState([]);
  const [domaines, setDomaines] = useState([]);
  const [results, setResults] = useState([]);

  // üîπ R√©cup√©rer toutes les donn√©es pour filtrage
  useEffect(() => {
    const fetchAllData = async () => {
      try {
        const res = await axios.get("http://localhost:3001/api/reglementation");
        setAllData(res.data);
        setTitres([...new Set(res.data.map(i => i.titre))]);
        setDomaines([...new Set(res.data.map(i => i.domaine))]);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAllData();
  }, []);

  // üîπ Filtrer les titres selon le domaine s√©lectionn√©
  const handleDomaineChange = (value) => {
    setSelectedDomaine(value);
    const titresFiltres = value
      ? allData.filter(i => i.domaine === value).map(i => i.titre)
      : allData.map(i => i.titre);
    setTitres([...new Set(titresFiltres)]);
    if (!titresFiltres.includes(titre)) setTitre("");
  };

  // üîπ Filtrer les domaines selon le titre s√©lectionn√©
  const handleTitreChange = (value) => {
    setTitre(value);
    const domainesFiltres = value
      ? allData.filter(i => i.titre === value).map(i => i.domaine)
      : allData.map(i => i.domaine);
    setDomaines([...new Set(domainesFiltres)]);
    if (!domainesFiltres.includes(selectedDomaine)) setSelectedDomaine("");
  };

  // üîπ Requ√™te recherche backend
  const handleSearch = async () => {
    try {
      const params = { search, titre, domaine: selectedDomaine };
      const res = await axios.get("http://localhost:3001/api/reglementation", { params });
      setResults(res.data);
    } catch (err) {
      console.error(err);
      alert("Erreur recherche !");
    }
  };

  // üîπ Mettre √† jour les champs d'audit localement
  const handleChange = (id, field, value) => {
    setResults(prev => prev.map(r => r.id === id ? { ...r, [field]: value } : r));
  };

  // üîπ Sauvegarde audit
  const saveAudit = async (item) => {
    try {
      await axios.post("http://localhost:3001/api/audit", {
        reglementation_id: item.id,
        conformite: item.conformite,
        risque: item.risque,
        faisabilite: item.faisabilite,
        plan_action: item.plan_action,
        deadline: item.deadline,
        owner: item.owner
      });
      alert("Audit sauvegard√© !");
    } catch (err) {
      console.error(err);
      alert("Erreur sauvegarde audit !");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Recherche R√©glementation</h1>

      <input
        type="text"
        placeholder="Mot-cl√©"
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ marginRight: 10 }}
      />

      {/* Domaine en premier */}
      <select
        value={selectedDomaine}
        onChange={e => handleDomaineChange(e.target.value)}
        style={{ marginRight: 10 }}
      >
        <option value="">-- Choisir un domaine --</option>
        {domaines.map((d, i) => (
          <option key={i} value={d}>{d}</option>
        ))}
      </select>

      {/* Titre ensuite */}
      <select
        value={titre}
        onChange={e => handleTitreChange(e.target.value)}
        style={{ marginRight: 10 }}
      >
        <option value="">-- Choisir un titre --</option>
        {titres.map((t, i) => (
          <option key={i} value={t}>{t}</option>
        ))}
      </select>

      <button onClick={handleSearch}>Rechercher</button>

      <table border="1" cellPadding="5" style={{ marginTop: 20, borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr>
            <th>DomaineTitre</th><th></th><th>Exigence</th>
            <th>Conformit√©</th><th>Risque</th><th>Faisabilit√©</th>
            <th>Plan Action</th><th>Deadline</th><th>Owner</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {results.length === 0 ? (
            <tr><td colSpan="10" style={{ textAlign: 'center' }}>Aucun r√©sultat</td></tr>
          ) : results.map(item => (
            <tr key={item.id}>
              <td>{item.domaine}</td>
              <td>{item.titre}</td>
              <td>{item.exigence}</td>

              <td>
                <select value={item.conformite || ""} onChange={e => handleChange(item.id, "conformite", e.target.value)}>
                  <option value="">--Choisir--</option>
                  <option value="Conforme">Conforme</option>
                  <option value="Non Conforme">Non Conforme</option>
                  <option value="Non Applicable">Non Applicable</option>
                </select>
              </td>

              <td>
                <select value={item.risque || ""} onChange={e => handleChange(item.id, "risque", e.target.value)}>
                  <option value="">--Choisir--</option>
                  <option value="Faible">Faible</option>
                  <option value="Moyen">Moyen</option>
                  <option value="√âlev√©">√âlev√©</option>
                </select>
              </td>

              <td>
                <select value={item.faisabilite || ""} onChange={e => handleChange(item.id, "faisabilite", e.target.value)}>
                  <option value="">--Choisir--</option>
                  <option value="Facile">Facile</option>
                  <option value="Moyen">Moyen</option>
                  <option value="Difficile">Difficile</option>
                </select>
              </td>

              <td><input type="text" value={item.plan_action || ""} onChange={e => handleChange(item.id, "plan_action", e.target.value)} /></td>
              <td><input type="date" value={item.deadline || ""} onChange={e => handleChange(item.id, "deadline", e.target.value)} /></td>
              <td><input type="text" value={item.owner || ""} onChange={e => handleChange(item.id, "owner", e.target.value)} /></td>
              <td><button onClick={() => saveAudit(item)}>Sauvegarder</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ReglementationSearch;
